CREATE TABLE Customer (customerId integer GENERATED BY DEFAULT AS IDENTITY (START WITH 1), firstName varchar(255), lastName varchar(255), email varchar(255), PRIMARY KEY (customerId));
CREATE TABLE SeatHold (seatHoldId integer GENERATED BY DEFAULT AS IDENTITY (START WITH 1), reservationId integer NOT NULL, seatId integer NOT NULL, hold varchar(10), PRIMARY KEY (seatHoldId));
CREATE TABLE Event (eventId integer GENERATED BY DEFAULT AS IDENTITY (START WITH 1), eventName varchar(255), eventTimeStamp timestamp, PRIMARY KEY (eventId));
CREATE TABLE Level (levelId integer GENERATED BY DEFAULT AS IDENTITY (START WITH 1), levelName varchar(255), price integer, numberOfRows integer, seatsinRow integer, PRIMARY KEY (levelId));
CREATE TABLE Reservation (reservationId integer GENERATED BY DEFAULT AS IDENTITY (START WITH 1), customerId integer, eventId integer NOT NULL, reservationTimeStamp timestamp, confirmationCode varchar(255), PRIMARY KEY (reservationId));
CREATE TABLE Seat (seatId integer GENERATED BY DEFAULT AS IDENTITY (START WITH 1), seatNumber varchar(255), held varchar(255), levelId integer NOT NULL, PRIMARY KEY (seatId));
ALTER TABLE Reservation ADD CONSTRAINT "Reservation_FK1" FOREIGN KEY (customerId) REFERENCES Customer (customerId);
ALTER TABLE Reservation ADD CONSTRAINT "Reservation_FK2" FOREIGN KEY (eventId) REFERENCES Event (eventId);
ALTER TABLE SeatHold ADD CONSTRAINT "SeatHold_FK1" FOREIGN KEY (reservationId) REFERENCES Reservation (reservationId);
ALTER TABLE SeatHold ADD CONSTRAINT "SeatHold_FK2" FOREIGN KEY (seatId) REFERENCES Seat (seatId);
ALTER TABLE Seat ADD CONSTRAINT "Seat_FK1" FOREIGN KEY (levelId) REFERENCES Level (levelId);
